import { Button, Checkbox, Form,Container,Card,Grid,Icon } from 'semantic-ui-react'
import axios from 'axios'
import { Link } from 'react-router-dom';

export default class MyStudents extends Component {


    state={
        allusers:[],
        myteacherinfo:{},
        studentswithMyEmail:[]
    }
componentDidMount = () => {
    // this.getAllUserData()

    // this.getTeacherInfo()
    this.getRealStudents()
    this.getMyData()
  }

  getRealStudents = () => {
    axios.get('/api/user/profiles',{
        headers: { authorization: localStorage.getItem('token') },
      })
      .then((res)=>{

    this.setState({realData:res.data})
    console.log(res.data,'res')
    })
    }


    getMyData = () => {
        axios.get('/api/user/profile', {
            headers: { authorization: localStorage.getItem('token') },
          })
          .then((response)=>{
              console.log(response.data,'what is my data?')

    this.setState({userData:response.data})
        })
    }



        getAllUsers = () => {
            axios.get('/api/user/getalluser')
            .then((res)=>{
                this.setState({allusers:res.data})
                console.log(res.data,'all students')
            })
        }

        getCurrentTeacherInfo = () => {
         axios.get('/api/teacher/teacherprofile',{
            headers: { authorization: localStorage.getItem('teachertoken') },
          })
          .then((res)=>{
         this.setState({myteacherinfo:res.data})


        })
        }




    render() {
        return (
            <div >

                <h2 style={{textAlign:'center'}}> Hello {this.state.myteacherinfo.email}</h2>
              <p> my students</p>
              {this.state.allusers?
              this.state.allusers.map((item)=>(

             <p >

                {item.myTeachers?
                item.myTeachers.length? 
                item.myTeachers.email == this.state.myteacherinfo.email?


                item.email

                :null:null:null}


             </p>

              )):null}



            </div>
        )
    }
}






