import {
  GET_ALL_STOCKS,
  GET_ALL_STOCKS_ERROR,
  GET_USER_STOCKS,
  GET_USER_TODOS_ERROR,
  ADD_TODO_ERROR,
  UPDATE_TODO_BY_ID_ERROR,
  DELETE_TODO_BY_ID_ERROR,
} from '../actions/types';



const INITIAL_STATE = {
  allTodos: [],
  userTodos: [],
  addTodoError: '',
  getAllTodosError: '',
  getUserTodosServerError: '',
  getUserTodosClientError: '',
  updateTodoError: '',
  deleteTodoByIdError: '',
};


export default function (state = INITIAL_STATE, action) {
  switch (action.type) {
    case GET_ALL_STOCKS:
      return {...state, allTodos: action.payload, getAllTodosError: '' };
    case GET_ALL_STOCKS_ERROR:
      return {...state, getAllTodosError: action.payload };
    case GET_USER_STOCKS:
      return {
        ...state,
        userTodos: action.payload,
        getUserTodosClientError: '',
        getUserTodosServerError: '',
        addTodoError: '',
        updateTodoError: '',
        deleteTodoByIdError: '',
      };
    case GET_USER_TODOS_ERROR:
      return {...state, getUserTodosServerError: action.serverError, getUserTodosClientError: action.userError };
    case ADD_TODO_ERROR:
      return {...state, addTodoError: action.payload };
    case UPDATE_TODO_BY_ID_ERROR:
      return {...state, updateTodoError: action.payload };
    case DELETE_TODO_BY_ID_ERROR:
      return {...state, deleteTodoByIdError: action.payload };
    default:
      return state;
  }
}
