import {
    GET_USER_STOCKS,
    GET_USER_STOCKS_ERROR
   } from '../actions/types';

  const INITIAL_STATE = {
    getUserStocks: [],
    getUserStocksErrors: '',
  };

  export default function (state = INITIAL_STATE, action) {
    switch (action.type) {
      case GET_USER_STOCKS:
        return { ...state, getUserStocks: action.payload, getUserStocksErrors: '' };
      case GET_USER_STOCKS_ERROR:
        return { ...state, getUserStocksErrors: action.payload };
      default:
        return state;
    }
  };
