import React, { Component } from 'react'
import { getUserUploads} from '../../actions/stocks';import { reduxForm, Field } from 'redux-form';
import { connect } from 'react-redux';
import { Grid,Divider,Header, Form, Segment, Message, List, Pagination, Button, Icon } from 'semantic-ui-react';
import FormContainer from './../searchForm/formContainer/index'
import { compose } from 'redux';
import { Helmet } from 'react-helmet';
import { getUserData, getOtherUsers } from './../../actions/profile';



import axios from 'axios';

// import UserTodoListItems from './userWatchListItem';

// import requireAuth from '../../hoc/requireAuth';


import { getUserTodos, updateTodoCompletedById, deleteTodoById } from '../../actions/stocks';

import { ADD_TODO_ERROR, ADD_TODO} from '../../actions/types';


class Uploads extends Component {

    componentDidMount() {
        this.props.getUserUploads();
      }

   onSubmit = () => {
       axios.post()
   }

    // renderAddTodo = ({ input, meta }) => {
    //     return (
    //      <>

    //        <Form.Input
    //          {...input}
    //          error={ meta.touched && meta.error }
    //          fluid
    //          autoComplete='off'
    //          placeholder='Add your stock'
    //          icon='dollar'
    //          iconPosition='left'

    //        />
    //      </>
    //    )
    //  }
    render() {
        const {handleSubmit} = this.props;

        return (
            <div style={{margin:'100px 45px 0 45px'}}>

<div style={{padding:'0 25px 34px 0'}}>


<Header as="h2"  textAlign="center" >
  <p style={{color:'white'}}>My WatchList </p> 
</Header>
   <form size='large' onSubmit={handleSubmit(this.onSubmit)} encType='multipart/form-data'>
       <Segment   >
         {/* <Field
         name="text"
         component={this.renderAddTodo}
         /> */}
         <input
         style={{margin:'0 0 25px 430px'}}
         type='file'
         name='file'
        //  id='file'
         className='customer-file-input'>
         </input>
         <Button
         type='submit'
         fluid
         color='purple'
         content='add'
         />
       </Segment>
       </form>
       <List animated divided selection >
         {/* <UserTodoListItems 
         todos={this.props.todos.slice(this.state.start,this.state.end)}
         handleUpdate={this.props.updateTodoCompletedById}
         handleDelete={this.props.deleteTodoById}
         /> */}
       </List>
       {
         this.props.uploads.length <= 9 ? 
         null
         : <Pagination
         pointing
         secondary
         totalPages={Math.ceil(this.props.todos.length / 10)}
         onPageChange={(event,data)=> this.handlePageChange(event,data)}
         activePage={this.state.activePage}
         />
       }       
</div>
            </div>
        )
    }
}


function mapStateToProps({ uploads: { userUploads, getUserTodosServerError, getUserTodosClientError, deleteTodoByIdError}}) {
    return {
      uploads: userUploads,
      clientError: getUserTodosClientError,
      serverError: getUserTodosServerError,
      deleteTodoByIdError,
    };
  }
   export default compose(
    reduxForm({ form: 'addTodo' }),
    connect(mapStateToProps, { getUserUploads,updateTodoCompletedById, deleteTodoById,getUserData, getOtherUsers })
  )(Uploads);
