      <Table.Body>
   {this.props.allTodos.map(({_id,text,dateCreated,name})=>(
     <Table.Row key={_id}>
         <Table.Cell> <h3>{text}</h3></Table.Cell>
         <Table.Cell>{moment(dateCreated).fromNow()}</Table.Cell>

        </Table.Row>
   ))}

      </Table.Body>
     </Table> */}


  <Container>
        <Grid columns={2}>

    <h2 style={{margin:'100px 0 0 0'}}> 
     </h2>
         </Grid>
        <Grid.Row>

          <Grid columns={2} Container stackable centered>
            <Grid.Column width={5}>
              <Responsive minWidth={768}>

              <Card >
    <Card.Content textAlign='center' > <h2 >{this.state.userData.email}</h2> 

    </Card.Content>
    <Card.Content >      <p style={{textAlign:'center'}}>card description</p>
</Card.Content>
     <Card.Content extra>
      <Icon name='user' /> 
    </Card.Content>
  </Card>


                </Responsive>

            </Grid.Column>

            <Grid.Column width={11} className='customScroll'>

              <Card.Group fluid stackable doubling itemsPerRow={1} >


                {this.state.filteredUsers.map((item)=>(

                  <div>
<Card>
                <Card.Content  > <h2> {item}</h2> </Card.Content>
    <Card.Content description={description} />
    <Card.Content extra>
      <Icon name='user' />4 Friends
    </Card.Content>
  </Card>

                  </div>
                ))}


              </Card.Group>
            </Grid.Column>
          </Grid>
        </Grid.Row>
      </Container>


   </div>
   </div>

    )
  };


  render() {
      return (

      <div>

       <List celled selection size='huge'>
        {this.props.getAllTodosError ? <Message negative header={this.props.getAllTodosError}/> : null}
      { this.renderList() }

       </List>

       {this.props.allTodos.length <= 9 ? 
null     

:
<Pagination
                pointing
                secondary
                totalPages={Math.ceil(this.props.allTodos.length / 10)}
                onPageChange={(event,data)=> this.handlePageChange(event,data)}
                activePage={this.state.activePage}
                />

              }
       </div>

    );
  }
}

function mapStateToProps(state) {
  return { allTodos: state.todos.allTodos, getAllTodosError: state.todos.getAllTodosError };
}

export default connect(mapStateToProps, { getAllTodos,getUserTodos })(AllTodosList);

