import {
  GET_ALL_STOCKS,
  GET_ALL_STOCKS_ERROR,
  GET_USER_STOCKS,
  GET_USER_STOCKS_ERROR,
  ADD_STOCKS_ERROR,
  UPDATE_STOCK_BY_ID_ERROR,
  DELETE_STOCK_BY_ID_ERROR,
} from '../actions/types';



const INITIAL_STATE = {
  allTodos: [],
  userStocks: [],
  addTodoError: '',
  getAllTodosError: '',
  getUserStocksServerError: '',
  getUserTodosClientError: '',
  updateTodoError: '',
  deleteTodoByIdError: '',
};


export default function (state = INITIAL_STATE, action) {
  switch (action.type) {
    case GET_ALL_STOCKS:
      return {...state, allTodos: action.payload, getAllTodosError: '' };
    case GET_ALL_STOCKS_ERROR:
      return {...state, getAllTodosError: action.payload };
    case GET_USER_STOCKS:
      return {
        ...state,
        userStocks: action.payload,
        getUserTodosClientError: '',
        getUserStocksServerError: '',
        addTodoError: '',
        updateTodoError: '',
        deleteTodoByIdError: '',
      };
    case GET_USER_STOCKS_ERROR:
      return {...state, getUserStocksServerError: action.serverError, getUserTodosClientError: action.userError };
    case ADD_STOCKS_ERROR:
      return {...state, addTodoError: action.payload };
    case UPDATE_STOCK_BY_ID_ERROR:
      return {...state, updateTodoError: action.payload };
    case DELETE_STOCK_BY_ID_ERROR:
      return {...state, deleteTodoByIdError: action.payload };
    default:
      return state;
  }
}
