import axios from 'axios';
import React, { Component } from 'react'
import { Helmet } from 'react-helmet';
import ImageUploader from 'react-images-upload';



export default class UpdateProfile extends Component {

state={
    pictures:[],
    fileName:""
}


onDrop = (picture) => {
        this.setState({
            pictures: this.state.pictures.concat(picture),
        },()=>{
            console.log(this.state.pictures,'hello')
        });
    }

handleRequest = () => {
    console.log(this.state.pictures[0],'testing ')
    // const {fileName } = this.state 
    let data = new FormData();
    data.append(`${this.state.pictures}`,this.state.pictures,this.state.pictures[0].name)


    axios.post('/api/user/myimages', {fileName:this.state.pictures[0].name},{ headers: { 'authorization': localStorage.getItem('token')}})
    .then((response)=>{
        console.log(response, 'image item')
    })
}


    render() {
        return (
            <div>

<Helmet>
   <style>{'body { background-color:#532f8c;  }'}</style>

         </Helmet>


         <div style={{margin:'80px 20px 0px 20px',backgroundColor:"whitesmoke",minHeight:"80vh"}}>
             <h2 style={{display:'flex',justifyContent:"center",padding:"40px 0 0 0"}}>

                  upload file

                  </h2>



                  <ImageUploader
                withIcon={true}
                withPreview={true}
                buttonText='select image'
                onChange={this.onDrop}
                imgExtension={['.jpg', '.gif', '.png',]}
                maxFileSize={5242880}
            />


<button className='axiosRequestImages' onClick={this.handleRequest}> axios request</button>

         </div>


            </div>
        )
    }
}
